mapper.select:
        SELECT
        

--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail

        FROM tushu  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                and a.tushu_name like CONCAT('%',#{params.tushuName},'%')
            
            
                and a.tushu_zuozhe like CONCAT('%',#{params.tushuZuozhe},'%')
            
            
                and a.tushu_chubanshe like CONCAT('%',#{params.tushuChubanshe},'%')
            
            
                and a.tushu_types = #{params.tushuTypes}
            
            
                  and a.tushu_kucun_number >= #{params.tushuKucunNumberStart}   
            
            
                  and a.tushu_kucun_number <= #{params.tushuKucunNumberEnd}   
            
             
                and a.tushu_kucun_number = #{params.tushuKucunNumber}
             
            
                  and a.tushu_old_money >= #{params.tushuOldMoneyStart}   
            
            
                  and a.tushu_old_money <= #{params.tushuOldMoneyEnd}   
            
            
                  and a.tushu_new_money >= #{params.tushuNewMoneyStart}   
            
            
                  and a.tushu_new_money <= #{params.tushuNewMoneyEnd}   
            
            
                  and a.tushu_clicknum >= #{params.tushuClicknumStart}   
            
            
                  and a.tushu_clicknum <= #{params.tushuClicknumEnd}   
            
             
                and a.tushu_clicknum = #{params.tushuClicknum}
             
            
                and a.shangxia_types = #{params.shangxiaTypes}
            
            
                  and a.tushu_delete >= #{params.tushuDeleteStart}   
            
            
                  and a.tushu_delete <= #{params.tushuDeleteEnd}   
            
             
                and a.tushu_delete = #{params.tushuDelete}
             
            
                and a.tushu_content like CONCAT('%',#{params.tushuContent},'%')
            

                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
            
        

        order by a.${params.orderBy} desc
